// ProductDetailPage.js
import React, { useState, useEffect } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import Nav from '../Nav';
import Footer from '../Components/AppFooter/footer';
import './ProductDetailPage.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCartShopping, faBagShopping } from '@fortawesome/free-solid-svg-icons';
import CartModal from './CartModal';

const ProductDetailPage = () => {
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [reviews, setReviews] = useState([]);
  const [availability, setAvailability] = useState('Unknown');
  const [relatedProducts, setRelatedProducts] = useState([]);
  const [category, setCategory] = useState([]);
  const [showModal, setShowModal] = useState(false);

  const { productId } = useParams();

  useEffect(() => {
    axios.get(`http://localhost:8081/api1/products/${productId}`)
      .then(response => {
        setProduct(response.data);
        setCategory(response.data.category);
        if (response.data.quantity >= 10) {
          setAvailability('High Stock');
        } else if (response.data.quantity > 0 && response.data.quantity < 10) {
          setAvailability('Low Stock');
        } else {
          setAvailability('Out of Stock');
        }
      })
      .catch(error => {
        console.error('Error fetching product detail:', error);
      });

    axios.get(`http://localhost:8081/api/products`)
      .then(response => {
        setRelatedProducts(response.data);
      })
      .catch(error => {
        console.error('Error fetching related products:', error);
      });

    const fetchedReviews = [
      { customerName: 'Sam', comment: 'Great product!', rating: 5 },
      { customerName: 'Neha', comment: 'Could be better.', rating: 3 },
    ];
    setReviews(fetchedReviews);
  }, [productId]);

  const handleIncreaseQuantity = () => {
    setQuantity(quantity + 1);
  };

  const handleDecreaseQuantity = () => {
    if (quantity > 1) {
      setQuantity(quantity - 1);
    }
  };

  const handleAddToCart = () => {
    const totalPrice = product.price * quantity;

    axios.post('http://localhost:8081/api11/products/cart', {
      product_name: product.name,
      quantity,
      category: category,
      price: totalPrice,
      timestamp: product.timestamp
    })
      .then(response => {
        console.log('Product added to cart successfully');
        setShowModal(true); // Show modal on successful add to cart
      })
      .catch(error => {
        console.error('Error adding product to cart:', error);
      });
  };

  const handleGoToCart = () => {
    setShowModal(false);
    navigate('/addCart');
  };

  if (!product) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <Nav />
      <div className="product-container">
        <h>More Products</h>
        <div className="product-details">
          <img className="product-image" src={`http://localhost:8081/${product.image}`} alt={product.name} />
          <div className="product-info">
            <h2>{product.name}</h2>
            <p>Price: Rs.{product.price}</p>
            <p1>{product.description}</p1>
            <p style={{ color: 'black' }}>Category: {category}</p>
            <p style={{ color: 'grey', fontSize: '16px' }}>Availability: {availability ? 'In Stock' : 'Out of Stock'}</p>
            <div className="quantity-control">
              <span>Kilogram:</span>
              <button onClick={handleDecreaseQuantity}>-</button>
              <span>{quantity}</span>
              <button onClick={handleIncreaseQuantity}>+</button>
            </div>
            <div className='new-button'>
              <div className="add-to-cart-button">
                <button onClick={handleAddToCart} className='buttonStyle'>
                  <FontAwesomeIcon icon={faCartShopping} /> Add to Cart
                </button>
              </div>
              <div className="buy-now-button">
                <button onClick={handleAddToCart} className='buyNowButton'>
                  <FontAwesomeIcon icon={faBagShopping} /> Buy Now
                </button>
              </div>
              <div className="other-product-container">
          {relatedProducts && relatedProducts.length > 0 && (
            <ul className="other-product-list">
              {relatedProducts.map(product => (
                <li key={product.id} className="other-product-item">
                  <Link to={`/product/${product.id}`} className="other-product-link">
                    <div className="other-product-thumbnail">
                      <img src={`http://localhost:8081/${product.image}`} alt={product.name} className="other-product-image" />
                    </div>
                    <span className="other-product-name">{product.name}</span>
                    <span className="other-product-price">Rs.{product.price}</span>
                  </Link>
                </li>
              ))}
            </ul>
          )}
        </div>
            </div>
          </div>
        </div>
      </div>
      <Footer />
      <CartModal show={showModal} onClose={() => setShowModal(false)} onGoToCart={handleGoToCart} />
    </div>
  );
};

export default ProductDetailPage;
