import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './UserAddtocart.css'; // Import the CSS file
import Nav from '../Nav';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTimes } from '@fortawesome/free-solid-svg-icons';

function Cart() {
  const [cartItems, setCartItems] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const [selectedProducts, setSelectedProducts] = useState([]);
  const [showDetailsDialog, setShowDetailsDialog] = useState(false); // State to control the visibility of Details dialog
  const [buyerName, setBuyerName] = useState('');
  const [buyerPhoneNumber, setBuyerPhoneNumber] = useState('');
  const [buyerLocation, setBuyerLocation] = useState('');
  const [showCheckoutDialog, setShowCheckoutDialog] = useState(false);

  useEffect(() => {
    fetchCartItems(); // Fetch cart items when component mounts
  }, []); // Empty dependency array to run once on mount

  const fetchCartItems = () => {
    fetch('http://localhost:8081/api11/products/cart')
      .then(response => {
        if (!response.ok) {
          throw new Error('Failed to fetch cart items. Status: ' + response.status);
        }
        return response.json();
      })
      .then(data => {
        // Create a copy of the current cart items array
        const updatedCartItems = [...cartItems];

        // Iterate over the fetched data
        data.forEach(fetchedItem => {
          // Check if the fetched item already exists in the cart
          const existingItemIndex = updatedCartItems.findIndex(item => item.id === fetchedItem.id);

          if (existingItemIndex !== -1) {
            // If the item exists, update its quantity
            updatedCartItems[existingItemIndex].quantity += fetchedItem.quantity;
          } else {
            // If the item doesn't exist, add it to the cart
            updatedCartItems.push(fetchedItem);
          }
        });

        // Update the cartItems state with the updated array
        setCartItems(updatedCartItems);
        setError(null); // Clear any previous errors
        setLoading(false); // Set loading to false once data is fetched
      })
      .catch(error => {
        console.error('Error fetching cart items:', error);
        setError('Error fetching cart items. Please try again later.'); // Set error state
        setLoading(false); // Set loading to false in case of error
      });
  };

  const handleAddToCart = (product) => {
    const existingItem = cartItems.find(item => item.id === product.id);
    if (existingItem) {
        // If product already exists, update quantity
        handleQuantityChange(product.id, existingItem.quantity + 1);
    } else {
        // If product doesn't exist, add it to cart
        fetch('http://localhost:8081/api13/products/cart', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(product),
        })
        .then(response => {
            if (!response.ok) {
                throw new Error('Failed to add product to cart');
            }
            // If the request is successful, fetch updated cart items
            fetchCartItems();
        })
        .catch(error => {
            console.error('Error adding product to cart:', error);
            setError('Error adding product to cart. Please try again.');
        });
    }
};

  const handleRemoveFromCart = (productId, price) => {
    fetch(`http://localhost:8081/api11/products/${productId}`, {
      method: 'DELETE',
    })
      .then(response => {
        if (!response.ok) {
          throw new Error('Failed to remove product from cart. Status: ' + response.status);
        }
        // If the request is successful, update the cartItems state to remove the item
        setCartItems(prevCartItems => prevCartItems.filter(item => item.id !== productId));
      })
      .catch(error => {
        console.error('Error removing product from cart:', error);
        setError('Error removing product from cart. Please try again.'); // Set error state
      });
  };

  const handleQuantityChange = (productId, newQuantity) => {
    // Prevent decreasing quantity below 0
    if (newQuantity < 0) {
      return;
    }



    // Make a PATCH request to update the quantity and price in the backend
    fetch(`http://localhost:8081/api11/products/${productId}`, {
      method: 'PATCH',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ quantity: newQuantity }),
    })
      .then(response => {
        if (!response.ok) {
          throw new Error('Failed to update quantity. Status: ' + response.status);
        }
        // If the request is successful, update the quantity and price in the cartItems state
        setCartItems(prevCartItems =>
          prevCartItems.map(item =>
            item.id === productId ? { ...item, quantity: newQuantity } : item
          )
        );
      })
      .catch(error => {
        console.error('Error updating quantity:', error);
        setError('Error updating quantity. Please try again.'); // Set error state
      });
  };



  // Calculate total price
  const totalPrice = cartItems.reduce((total, item) => total + item.price * item.quantity, 0);

  // Show loading indicator while data is being fetched
  if (loading) {
    return <p>Loading...</p>;
  }

  // Show error message if there's an error
  if (error) {
    return <p>{error}</p>;
  }


  const handleProceedToCheckout = () => {
    setShowCheckoutDialog(true);
  };

  const handleCheckoutCancel = () => {
    setShowCheckoutDialog(false);
  };

  const handlePlaceOrder = () => {
    const modifiedCartItems = cartItems.map(item => ({
      ...item,
      productName: item.product_name // Assuming product_name is the field containing the product name
    }));
    const orderDetails = {
      buyerName,
      buyerPhoneNumber,
      buyerLocation,
      cartItems: modifiedCartItems,
      totalPrice: calculateTotalPrice() // You need to implement this function

    };

    fetch('http://localhost:8081/api13/products/placeOrder', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(orderDetails),
    })
      .then(response => {
        if (!response.ok) {
          throw new Error('Failed to place order');
        }
        return response.json();
      })
      .then(data => {
        const modifiedCartItems = data.map(item => ({
          ...item,
          productName: item.product_name, // Assuming product_name is the field containing the product name
          category: item.category
        }));
        console.log('Order placed successfully:', data);
        // Clear cart and show success message
        setCartItems([]);
        setShowCheckoutDialog(false);
      })
      .catch(error => {
        console.error('Error placing order:', error);
        // Show error message to the user
      });
  };

  const calculateTotalPrice = () => {
    // Initialize total price
    let totalPrice = 0;

    // Loop through cart items and sum the prices
    cartItems.forEach(item => {
      totalPrice += item.price * item.quantity;
    });

    // Add shipping fee
    totalPrice += 250;

    return totalPrice;
  };


  // Render cart items if data fetching is successful
  return (
    <div>
      <Nav />
      <div className="cart-container">
        {cartItems.length > 0 ? (
          cartItems.map(item => (
            <div key={item.id} className="cart-item">
              {/* Product Image */}
              <img src={item.image} alt={item.product_name} className="product-image1" />

              {/* Product Details */}
              <div className="product-details1">
                <div className="product-name1">{item.product_name}</div>

                <div className="quantity-control1">
                  <button className="quantity-btn" onClick={() => handleQuantityChange(item.id, item.quantity - 1)}>-</button>
                  <span>{item.quantity}</span>
                  <button className="quantity-btn" onClick={() => handleQuantityChange(item.id, item.quantity + 1)}>+</button>
                </div>
                <div className="product-price1">Rs. {item.price * item.quantity}</div>
                <button className="remove-btn" onClick={() => handleRemoveFromCart(item.id, item.price)}>
                  <FontAwesomeIcon icon={faTimes} />
                </button>
              </div>
            </div>
          ))
        ) : (
          <div className="empty-cart">
            <t1>Cart is empty</t1>
          </div>
        )}
      </div>
      {/* Order Summary */}
      {cartItems.length > 0 && (
        <div className="order-summary">
          <h3>Order Summary</h3>
          {cartItems.map(item => (
            <div key={item.id}>
              <p>{item.product_name}: Quantity - {item.quantity}, Price - Rs. {item.price * item.quantity}</p>
            </div>
          ))}
          <p>Subtotal ({cartItems.length} items): <span>Rs. {totalPrice}</span></p>
          <p>Shipping Fee: <span>Rs. 250</span></p>
          <p>Total: <span>Rs. {totalPrice + 250}</span></p>
          <button onClick={() => handleProceedToCheckout(true)} style={{ borderRadius: '  10px' }}>
  PROCEED TO CHECKOUT
</button>

        </div>
      )}

{showCheckoutDialog && (
        <div className="checkout-dialog" style={{borderRadius:'20px', height:'450px'}}>
          <div className="checkout-header">
            <h2>Fill out this form and place your order!</h2>
            <button className="close-button" onClick={handleCheckoutCancel}>
              <FontAwesomeIcon icon={faTimes} />
            </button>
          </div>
          <form className="checkout-form" onSubmit={handlePlaceOrder} style={{marginTop:'10px'}}>
            <label htmlFor="buyerName" style={{marginRight:'8px'}}>Name:</label>
            <input type="text" id="buyerName" value={buyerName} onChange={e => setBuyerName(e.target.value)} required style={{padding:'8px',  height:'40px', borderRadius:'12px'}} />

            <label htmlFor="buyerPhoneNumber" style={{marginRight:'8px'}}>Phone Number:</label>
            <input type="text" id="buyerPhoneNumber" value={buyerPhoneNumber} onChange={e => setBuyerPhoneNumber(e.target.value)} required style={{padding:'8px',  height:'40px', borderRadius:'12px'}} />

            <label htmlFor="buyerLocation" style={{marginRight:'8px'}}>Location:</label>
            <input type="text" id="buyerLocation" value={buyerLocation} onChange={e => setBuyerLocation(e.target.value)} required  style={{padding:'8px',  height:'40px', borderRadius:'12px'}}/>

            <button type="submit" className='Buttonhere' style={{width:'400px', height:'40px', backgroundColor:'#06C265', borderRadius:'12px'}}>Place Order</button>
          </form>
        </div>
      )}
    </div>
  );
}



export default Cart;
