import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import AppHeader from './Components/AppHeader';
import { faBoxes, faShoppingCart, faDollarSign } from '@fortawesome/free-solid-svg-icons';
import FarmerSidebar from './Components/Sidebar/FarmerSidebar';

const FarmerLanding = () => {
  const [totalProducts, setTotalProducts] = useState(0);

  useEffect(() => {
    // Fetch total products from backend API
    fetchTotalProducts();
  }, []);

  const fetchTotalProducts = async () => {
    try {
      const response = await fetch('/api/farmer/products');
      if (response.ok) {
        const data = await response.json();
        setTotalProducts(data.totalProducts);
      } else {
        console.error('Failed to fetch total products');
      }
    } catch (error) {
      console.error('Error fetching total products:', error);
    }
  };

  const data = {
    // Your other data...
    products: {
      value: totalProducts, // Update total products value with fetched data
      increase: null,
      lastMonth: null
    },
    // Your other data...
  };

  // DashboardBox component remains unchanged...

  return (
    <div>
      <AppHeader />
      <div style={{ display: 'flex', marginTop: '20px' }}>
        {/* FarmerSidebar component */}
        <div style={{ flex: '0 0 20%', marginRight: '20px' }}>
          <FarmerSidebar />
        </div>

        {/* Dashboard content */}
        <div style={{ flex: '1' }}>
          <h2 style={{ color: '#4CAF50' }}>Dashboard</h2>
          {/* Grid layout for DashboardBox components */}
          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '15px', width: '90%', margin: '0 30px 0 0 ', marginTop: '20px', justifyContent: 'center', backgroundColor: '#fff', borderRadius: '20px', padding: '20px' }}>
            <DashboardBox
              title="Total Products"
              value={data.products.value}
              increase={data.products.increase}
              lastMonth={data.products.lastMonth}
              icon={faShoppingCart}
            />
            {/* Other DashboardBox components */}
          </div>
        </div>
      </div>
    </div>
  );
};

export default FarmerLanding;
