import React, { useState, useEffect } from 'react';
import './FarmerOrder.css';
import FarmerSidebar from '../../Components/Sidebar/FarmerSidebar';

const FarmerOrder = () => {
  const [orders, setOrders] = useState([]);
  const [searchInput, setSearchInput] = useState('');

  useEffect(() => {
    fetchOrders();
  }, []);

  const fetchOrders = async () => {
    try {
      const response = await fetch('http://localhost:8081/api11/farmer/orders');
      if (!response.ok) {
        throw new Error('Failed to fetch orders');
      }
      const data = await response.json();
      const transformedData = data.map(order => ({
        orderId: order.items[0].orderId,
        productName: order.items[0].productName,
        productId: order.items[0].productId,
        productQuantity: order.items[0].quantity,
        productCategory: order.items[0].category,
        productPrice: order.items[0].price,
        totalPrice: order.totalPrice,
        orderPlacedDate: order.orderDate,
        orderedBy: order.buyerName,
        contactInfo: order.buyerPhoneNumber,
        status: order.status,
      }));
      setOrders(transformedData);
    } catch (error) {
      console.error('Error fetching orders:', error);
      // Optionally set an error state to show a message to the user
    }
  };

  const updateOrderStatus = (orderId, newStatus) => {
    const updatedOrders = orders.map(order => (
      order.orderId === orderId ? { ...order, status: newStatus } : order
    ));
    setOrders(updatedOrders);
  };

  const handleSearchInputChange = (e) => {
    setSearchInput(e.target.value);
  };

  const filteredOrders = orders.filter(order =>
    order.productName && order.productName.toLowerCase().includes(searchInput.toLowerCase())
  );

  return (
    <div>
      <FarmerSidebar />
      <div style={{ marginLeft: '290px', padding: '20px' }}>
        <h1 style={{ marginBottom: '30px', fontWeight: '400', fontSize: '24px' }}>Orders</h1>
        <input
          type="text"
          value={searchInput}
          onChange={handleSearchInputChange}
          placeholder="Search by product name"
          style={{
            marginBottom: '20px',
            padding: '10px',
            fontSize: '14px',
            borderRadius: '4px',
            border: '1px solid #ddd',
            width: '400px',
            maxWidth: '100%'
          }}
        />
        <div className="farmer-order-container">
          <table>
            <thead>
              <tr>
                <th>Order ID</th>
                <th>Product Name</th>
                <th>Product ID</th>
                <th>Quantity</th>
                <th>Category</th>
                <th>Price</th>
                <th>Total Price</th>
                <th>Date</th>
                <th>Ordered By</th>
                <th>Phone Number</th>
                <th>Order Status</th>
              </tr>
            </thead>
            <tbody>
              {filteredOrders.map(order => (
                <tr key={order.orderId}>
                  <td>{order.orderId}</td>
                  <td>{order.productName}</td>
                  <td>{order.productId}</td>
                  <td>{order.productQuantity}</td>
                  <td>{order.productCategory}</td>
                  <td>{order.productPrice}</td>
                  <td>{order.totalPrice}</td>
                  <td>{order.orderPlacedDate}</td>
                  <td>{order.orderedBy}</td>
                  <td>{order.contactInfo}</td>
                  <td style={{ textAlign: 'center' }}>
                    <select
                      value={order.status}
                      onChange={(e) => updateOrderStatus(order.orderId, e.target.value)}
                      style={{
                        margin: '0 auto',
                        border: 'none',
                        display: 'block',
                        textAlign: 'center'
                      }}
                    >
                      <option value="Accepted">Accepted</option>
                      <option value="Shipping">Shipping</option>
                      <option value="Cancel">Cancelled</option>
                      <option value="Delivered">Delivered</option>
                    </select>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default FarmerOrder;
