import React, { useState, useEffect } from "react";
import axios from "axios";
import "./FarmerInventory.css";
import FarmerSidebar from "../../Components/Sidebar/FarmerSidebar";

const API_BASE_URL = "http://localhost:8081"; // Define your API base URL

const FarmerInventory = () => {
  const [products, setProducts] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [editingProduct, setEditingProduct] = useState(null);
  const [editedQuantity, setEditedQuantity] = useState("");
  const [showEditModal, setShowEditModal] = useState(false);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [selectedProductId, setSelectedProductId] = useState(null);
  const [errorMessage, setErrorMessage] = useState("");

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/api/products`);
      setProducts(response.data);
      setFilteredProducts(response.data);
    } catch (error) {
      console.error("Error fetching products:", error);
    }
  };

  const handleEdit = (id) => {
    setSelectedProductId(id);
    setShowEditModal(true);
    const editedProduct = products.find((product) => product.id === id);
    setEditedQuantity(editedProduct.quantity.toString());
  };

  const saveEditedQuantity = async () => {
    try {
      // Validate the edited quantity input
      const editedQuantityNumber = parseFloat(editedQuantity); // Try parsing the input as a number
      if (isNaN(editedQuantityNumber) || editedQuantityNumber < 0) {
        throw new Error("Quantity must be a numeric number");
      }

      await axios.put(`${API_BASE_URL}/apiedit/products/${selectedProductId}`, {
        quantity: editedQuantityNumber, // Use the parsed number for quantity
      });
      const updatedProducts = products.map((product) =>
        product.id === selectedProductId
          ? { ...product, quantity: editedQuantityNumber }
          : product
      );
      setProducts(updatedProducts);
      setFilteredProducts(updatedProducts); // Update filteredProducts too
      setShowEditModal(false);
    } catch (error) {
      console.error("Error updating product quantity:", error);
      // Display error message in red color
      setErrorMessage(error.message);
    }
  };

  const handleDelete = async (id) => {
    setSelectedProductId(id);
    setShowDeleteModal(true);
  };

  const confirmDelete = async () => {
    try {
      await axios.delete(`${API_BASE_URL}/api/products/${selectedProductId}`);
      const updatedProducts = products.filter(
        (product) => product.id !== selectedProductId
      );
      setProducts(updatedProducts);
      setFilteredProducts(updatedProducts);
    } catch (error) {
      console.error("Error deleting product:", error);
    } finally {
      setSelectedProductId(null);
      setShowDeleteModal(false);
    }
  };

  const handleCancelDelete = () => {
    setSelectedProductId(null);
    setShowDeleteModal(false);
  };

  const handleStockFilterChange = (value) => {
    setSearchQuery("");
    if (value === "low") {
      const lowStockProducts = products.filter(
        (product) => product.quantity < 5
      );
      setFilteredProducts(lowStockProducts);
    } else if (value === "high") {
      const highStockProducts = products.filter(
        (product) => product.quantity >= 10
      );
      setFilteredProducts(highStockProducts);
    } else {
      // If no filter is selected or an invalid filter value is received,
      // display all products
      setFilteredProducts(products);
    }
  };

  const handleSearch = (query) => {
    setSearchQuery(query);
    const filtered = products.filter((product) =>
      product.name.toLowerCase().includes(query.toLowerCase())
    );
    setFilteredProducts(filtered);
  };

  return (
    <div>
      <FarmerSidebar />

      <h1 style={{ fontWeight: "400", fontSize: "24px", marginLeft: "290px" }}>
        Inventory
      </h1>

      <div className="row-container">
        <div className="search-bar-container">
          <input
            type="text"
            placeholder="Search products..."
            value={searchQuery}
            onChange={(e) => handleSearch(e.target.value)}
            className="search-bar"
          />
        </div>

        <div
          className="filter-container"
          style={{ display: "flex", alignItems: "center", marginLeft: "442px" }}
        >
          <div className="button-group">
            <input
              type="radio"
              id="all-stock"
              name="stock-filter"
              value=""
              onChange={() => handleStockFilterChange("")}
              defaultChecked
              style={{ display: "none" }}
            />
            <label htmlFor="all-stock" className="button">
              All Stock
            </label>

            <input
              type="radio"
              id="low-stock"
              name="stock-filter"
              value="low"
              onChange={() => handleStockFilterChange("low")}
              style={{ display: "none" }}
            />
            <label htmlFor="low-stock" className="button">
              Low Stock
            </label>

            <input
              type="radio"
              id="high-stock"
              name="stock-filter"
              value="high"
              onChange={() => handleStockFilterChange("high")}
              style={{ display: "none" }}
            />
            <label htmlFor="high-stock" className="button">
              High Stock
            </label>
          </div>
        </div>
      </div>

      <div className="farmer-inventory">
        <table>
          <thead>
            <tr>
              <th>Product Name</th>
              <th>Product Category</th>
              <th>Product ID</th>
              <th>Product Price</th>
              <th>Product Quantity</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredProducts.map((product) => (
              <tr key={product.id}>
                <td>{product.name}</td>
                <td>{product.category}</td>
                <td>{product.id}</td>
                <td>{`Rs ${product.price}`}</td> {/* Display price with Rs prefix */}
                <td>
                  {editingProduct === product.id ? (
                    <input
                      type="text"
                      value={editedQuantity}
                      onChange={(e) => {
                        setEditedQuantity(e.target.value);
                        // Clear error message when user starts typing again
                        setErrorMessage("");
                      }}
                    />
                  ) : (
                    product.quantity
                  )}
                </td>
                <td>
                  {editingProduct === product.id ? (
                    <button
                      style={{
                        backgroundColor: "#4CAF50",
                        color: "white",
                        border: "none",
                        padding: "8px 12px",
                        borderRadius: "8px",
                        cursor: "pointer",
                        marginRight: "8px",
                        fontSize: "12px",
                      }}
                      onClick={saveEditedQuantity}
                    >
                      Save
                    </button>
                  ) : (
                    <>
                      <button
                        style={{
                          backgroundColor: "#4CAF50",
                          color: "white",
                          border: "none",
                          padding: "8px 14px",
                          borderRadius: "4px",
                          cursor: "pointer",
                          marginRight: "8px",
                          fontSize: "12px",
                          borderRadius: "8px",
                        }}
                        onClick={() => handleEdit(product.id)}
                      >
                        Edit
                      </button>
                      <button
                        className="delete-button"
                        style={{
                          backgroundColor: "#e2e2e2c0",
                          color: "black",
                          fontSize: "12px",
                          borderRadius: "8px",
                        }}
                        onClick={() => handleDelete(product.id)}
                      >
                        Delete
                      </button>
                    </>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {showEditModal && (
        <div className="edit-modal-container white-container">
          <div className="edit-modal-content">
            <h2>Edit Quantity</h2>
            <input
              type="text"
              value={editedQuantity}
              onChange={(e) => {
                setEditedQuantity(e.target.value);
                // Clear error message when user starts typing again
                setErrorMessage("");
              }}
            />
            {errorMessage && (
              <p style={{ color: "red" }}>{errorMessage}</p>
            )}
            <button
              onClick={saveEditedQuantity}
              style={{ fontSize: "14px", padding: "5px 10px" }}
            >
              Save
            </button>
          </div>
        </div>
      )}

      {showDeleteModal && (
        <div className="delete-modal-overlay">
          <div className="delete-modal-container white-container">
            <div className="delete-modal-content">
              <h2
                style={{
                  fontWeight: "normal",
                  fontSize: "18px",
                  textAlign: "center",
                  marginBottom: "20px",
                }}
              >
                Are you sure you want to delete this product?
              </h2>
              <div className="delete-modal-buttons" style={{ display: "flex", justifyContent: "center" }}>
                <button className="delete-confirm" onClick={confirmDelete} style={{ fontSize: "14px", padding: "5px 10px", marginRight: "10px" }}>
                  OK
                </button>
                <button className="delete-cancel" onClick={handleCancelDelete} style={{ fontSize: "14px", padding: "5px 10px", marginLeft: "10px" }}>
                  Cancel
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default FarmerInventory;
