import React, { useState } from 'react';

const EditProductDialog = ({ product, onSave, onClose }) => {
  const [editedProduct, setEditedProduct] = useState(product);
  const [errors, setErrors] = useState({});

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedProduct({ ...editedProduct, [name]: value });
    validateInput(name, value);
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();
    reader.onloadend = () => {
      setEditedProduct({ ...editedProduct, image: reader.result });
    };
    if (file) {
      reader.readAsDataURL(file);
    }
  };

  const validateInput = (name, value) => {
    let newErrors = { ...errors };
    switch (name) {
      case 'name':
        if (!value.match(/^[A-Z].*$/)) {
          newErrors[name] = 'Name must start with a capital letter.';
        } else {
          delete newErrors[name];
        }
        break;
      case 'price':
      case 'quantity':
        if (!value.match(/^\d+$/)) {
          newErrors[name] = 'Please enter a numeric value.';
        } else {
          delete newErrors[name];
        }
        break;
      default:
        break;
    }
    setErrors(newErrors);
  };

  const handleSave = () => {
    const isError = Object.keys(errors).length !== 0;
    if (!isError) {
      onSave(editedProduct);
    }
  };

  return (
    <div style={styles.overlay}>
      <div style={styles.dialog}>
        <h2 style={styles.title}>Edit Product</h2>
        <div style={styles.form}>
          <label htmlFor="name" style={styles.label}>Name:</label>
          <input
            id="name"
            type="text"
            name="name"
            value={editedProduct.name}
            onChange={handleInputChange}
            style={styles.input}
            aria-describedby={errors.name ? "nameError" : undefined}
          />
          {errors.name && <span id="nameError" style={styles.error}>{errors.name}</span>}

          <label htmlFor="description" style={styles.label}>Description:</label>
          <input
            id="description"
            type="text"
            name="description"
            value={editedProduct.description}
            onChange={handleInputChange}
            style={styles.input}
          />

          <label htmlFor="category" style={styles.label}>Category:</label>
          <select
            id="category"
            name="category"
            value={editedProduct.category}
            onChange={handleInputChange}
            style={styles.input}
          >
            <option value="">Select Category</option>
            <option value="dairy">Dairy Products</option>
            <option value="fruits">Fruits</option>
            <option value="vegetables">Vegetables</option>
          </select>

          <label htmlFor="price" style={styles.label}>Price:</label>
          <input
            id="price"
            type="text"
            name="price"
            value={editedProduct.price}
            onChange={handleInputChange}
            style={styles.input}
            aria-describedby={errors.price ? "priceError" : undefined}
          />
          {errors.price && <span id="priceError" style={styles.error}>{errors.price}</span>}

          <label htmlFor="quantity" style={styles.label}>Quantity:</label>
          <input
            id="quantity"
            type="text"
            name="quantity"
            value={editedProduct.quantity}
            onChange={handleInputChange}
            style={styles.input}
            aria-describedby={errors.quantity ? "quantityError" : undefined}
          />
          {errors.quantity && <span id="quantityError" style={styles.error}>{errors.quantity}</span>}

          <input
            type="file"
            accept="image/*"
            onChange={handleImageChange}
            style={{ ...styles.input, marginBottom: '25px' }}
          />
          {editedProduct.image && <img src={editedProduct.image} alt="Product" style={styles.image} />}
        </div>
        <div style={styles.buttonContainer}>
          <button onClick={handleSave} style={styles.saveButton}>Save</button>
          <button onClick={onClose} style={styles.cancelButton}>Cancel</button>
        </div>
      </div>
    </div>
  );
};

const styles = {
  overlay: {
    position: 'fixed',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
  dialog: {
    backgroundColor: '#fff',
    padding: '30px 30px 30px 40px',
    borderRadius: '8px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
    maxWidth: '450px',
    width: '90%',
    textAlign: 'center',
  },
  title: {
    marginBottom: '20px',
    color: '#333',
    borderBottom: '2px solid #ccc',
    paddingBottom: '10px',
  },
  form: {
    marginBottom: '20px',
    textAlign: 'left',
  },
  label: {
    display: 'block',
    marginBottom: '5px',
    color: '#555',
    fontWeight: 'bold',
  },
  input: {
    width: '90%',
    padding: '10px',
    border: '1px solid #ccc',
    borderRadius: '12px',
    marginBottom: '15px',
    fontSize: '14px',
  },
  error: {
    color: 'red',
    fontSize: '10px',
    marginLeft: '10px',
  },
  image: {
    maxWidth: '30%',
    height: '100px',
    marginBottom: '15px',
  },
  buttonContainer: {
    display: 'flex',
    justifyContent: 'center',
    gap: '10px',
  },
  saveButton: {
    padding: '12px 24px',
    border: 'none',
    borderRadius: '5px',
    backgroundColor: '#4CAF50',
    color: 'white',
    cursor: 'pointer',
    fontSize: '16px',
    transition: 'background-color 0.3s',
  },
  cancelButton: {
    padding: '12px 24px',
    border: 'none',
    borderRadius: '5px',
    backgroundColor: '#B01818',
    color: 'white',
    cursor: 'pointer',
    fontSize: '16px',
    transition: 'background-color 0.3s',
  },
  buttonHover: {
    saveButton: {
      backgroundColor: '#45a049',
    },
    cancelButton: {
      backgroundColor: '#9b1515',
    },
  },
};

export default EditProductDialog;
