import React, { useState, useEffect } from 'react';
import './AdminOrder.css';
import axios from 'axios';
import AppHeader from '../../Components/AppHeader/index.js';
import Sidebar from '../../Components/Sidebar/sidebar.js';

const AdminOrder = () => {
  const [orders, setOrders] = useState([]);
  const [searchInput, setSearchInput] = useState('');

  useEffect(() => {
    fetchOrders();
  }, []);

  const fetchOrders = async () => {
    try {
      const response = await axios.get('/api11/farmer/orders'); // Updated endpoint
      setOrders(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const handleSearchInputChange = (e) => {
    setSearchInput(e.target.value);
  };

  const filteredOrders = orders.filter(order =>
    order.items.join(', ').toLowerCase().includes(searchInput.toLowerCase())
  );

  return (
    <div>
      <AppHeader />
      <Sidebar adminName="Your Admin Name" />
      <div className="admin-order-container">
        <h1>Orders</h1>
        <input
          type="text"
          value={searchInput}
          onChange={handleSearchInputChange}
          placeholder="Search by product name"
        />
        <table>
          <thead>
            <tr>
              <th>Order Number</th>
              <th>Items Purchased</th>
              <th>Quantity</th>
              <th>Total Price</th>
              <th>Customer Name</th>
              <th>Customer Phone</th>
              <th>Customer Location</th>
              <th>Order Status</th>
            </tr>
          </thead>
          <tbody>
            {filteredOrders.map((order) => (
              <tr key={order.orderId}>
                <td>{order.orderId}</td>
                <td>{order.items.map(item => item.productName).join(', ')}</td>
                <td>{order.items.reduce((acc, item) => acc + item.quantity, 0)}</td>
                <td>{order.totalPrice}</td>
                <td>{order.buyerName}</td>
                <td>{order.buyerPhoneNumber}</td>
                <td>{order.buyerLocation}</td>
                <td>{order.status}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default AdminOrder;
