import React, { useState } from 'react';

const EditProductDialog = ({ product, onSave, onClose }) => {
  const [editedProduct, setEditedProduct] = useState(product);
  const [errors, setErrors] = useState({});

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedProduct({ ...editedProduct, [name]: value });
    validateInput(name, value);
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();
    reader.onloadend = () => {
      setEditedProduct({ ...editedProduct, image: reader.result });
    };
    if (file) {
      reader.readAsDataURL(file);
    }
  };

  const validateInput = (name, value) => {
    let newErrors = { ...errors };
    switch (name) {
      case 'name':
        if (!value.match(/^[A-Z].*$/)) {
          newErrors[name] = 'Name must start with a capital letter.';
        } else {
          delete newErrors[name];
        }
        break;
      case 'price':
      case 'quantity':
        if (!value.match(/^\d+$/)) {
          newErrors[name] = 'Please enter a numeric value.';
        } else {
          delete newErrors[name];
        }
        break;
      default:
        break;
    }
    setErrors(newErrors);
  };


  const handleSave = async () => {
    try {
      const formData = new FormData();
      formData.append('name', editedProduct.name);
      formData.append('description', editedProduct.description);
      formData.append('category', editedProduct.category);
      formData.append('price', editedProduct.price);
      formData.append('quantity', editedProduct.quantity);

      // If editedProduct.image is a file, append it to the FormData
      if (editedProduct.image instanceof File) {
        formData.append('image', editedProduct.image);
      }

      // Perform the save operation with axios
      await axios.put(`http://localhost:8081/api/products/${editedProduct.id}`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      onSave(editedProduct); // Assuming onSave function updates the product in the parent component

      onClose(); // Close the dialog
    } catch (error) {
      console.error('Error saving product:', error);
    }
  };


  return (
    <div style={{ position: 'fixed', top: 0, left: 0, width: '100%', height: '100%', backgroundColor: 'rgba(0, 0, 0, 0.5)', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
      <div style={{ backgroundColor: '#fff', padding: '30px 30px 30px 40px', borderRadius: '8px', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)', maxWidth: '450px', width: '450px', textAlign: 'center' }}>
        <h2 style={{ marginBottom: '20px', color: '#333', borderBottom: '2px solid #ccc', paddingBottom: '10px' }}>Edit Product</h2>
        <div style={{ marginBottom: '20px', textAlign: 'left' }}>
          <label style={{ display: 'block', marginBottom: '5px', color: '#555', fontWeight: 'bold' }}>Name:</label>
          <input type="text" name="name" value={editedProduct.name} onChange={handleInputChange} style={{ width: '90%', padding: '10px', border: '1px solid #ccc', borderRadius: '12px', marginBottom: '15px', fontSize: '14px' }} />
          {errors.name && <span style={{ color: 'red', fontSize: '10px' , marginLeft: '10px', }}>{errors.name}</span>}
          <label style={{ display: 'block', marginBottom: '5px', color: '#555', fontWeight: 'bold' }}>Description:</label>
          <input type="text" name="description" value={editedProduct.description} onChange={handleInputChange} style={{ width: '90%', padding: '10px', border: '1px solid #ccc', borderRadius: '12px', marginBottom: '15px', fontSize: '14px' }} />
          <label style={{ display: 'block', marginBottom: '5px', color: '#555', fontWeight: 'bold' }}>Category:</label>
          <select name="category" value={editedProduct.category} onChange={handleInputChange} style={{ width: '90%', padding: '10px', border: '1px solid #ccc', borderRadius: '12px', marginBottom: '15px', fontSize: '14px' }}>
            <option value="">Select Category</option>
            <option value="dairy">Dairy Products</option>
            <option value="fruits">Fruits</option>
            <option value="vegetables">Vegetables</option>
          </select>
          <label style={{ display: 'block', marginBottom: '5px', color: '#555', fontWeight: 'bold' }}>Price:</label>
          <input type="text" name="price" value={editedProduct.price} onChange={handleInputChange} style={{ width: '90%', padding: '10px', border: '1px solid #ccc', borderRadius: '12px', marginBottom: '15px', fontSize: '14px' }} />
          {errors.price && <span style={{ color: 'red', fontSize: '10px' }}>{errors.price}</span>}
          <label style={{ display: 'block', marginBottom: '5px', color: '#555', fontWeight: 'bold' }}>Quantity:</label>
          <input type="text" name="quantity" value={editedProduct.quantity} onChange={handleInputChange} style={{ width: '90%', padding: '10px', border: '1px solid #ccc', borderRadius: '12px', marginBottom: '15px', fontSize: '14px' }} />
          {errors.quantity && <span style={{ color: 'red', fontSize: '10px' }}>{errors.quantity}</span>}
          <input type="file" accept="image/*" onChange={handleImageChange} style={{ width: '90%', marginBottom: '25px', fontSize: '14px' }} />
          {editedProduct.image && <img src={editedProduct.image} alt="Product" style={{ maxWidth: '30%', height: '100px', marginBottom: '15px' }} />}
        </div>
        <div>
          <button onClick={handleSave} style={{ padding: '12px 24px', border: 'none', borderRadius: '5px', backgroundColor: '#4CAF50', color: 'white', cursor: 'pointer', marginRight: '10px', fontSize: '16px' }}>Save</button>
          <button onClick={onClose} style={{ padding: '12px 24px', border: 'none', borderRadius: '5px', backgroundColor: '#B01818', color: 'white', cursor: 'pointer', fontSize: '16px' }}>Cancel</button>
        </div>
      </div>
    </div>
  );
};

export default EditProductDialog;
